sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

var nITasksDueSoon = new GlideRecord('x_58872_needit_needit_task');
nITasksDueSoon.addQuery('due_date','>=',rightNow);
nITasksDueSoon.addQuery('due_date','<',gs.hoursAgo(-24));
nITasksDueSoon.addQuery('state','!=',3);

nITasksDueSoon.query();
// Write a log message for each NeedIt Tasks Due Soon Record
while(nITasksDueSoon.next()){
	gs.info("NeedIt Tasks Due Soon record = " + nITasksDueSoon.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',nITasksDueSoon,nITasksDueSoon.number,gs.getUserName());
}
